class HitCounterCodeType {
    const BasicStyle="BasicStyle";//A basic style hit counter.
    const CustomCode="CustomCode";//Reserved for internal or future use
    const GreenLED="GreenLED";//A green LED, computer-style hit counter (US only). Non-US sites will return errors if they use GreenLED as input, and should use RetroStyle instead.
    const Hidden="Hidden";//A hidden hit counter (US only). The number of page views will only be available to the item's seller. For faster "View Item" page loads, use HiddenStyle.
    const HiddenStyle="HiddenStyle";//A hidden hit counter. The number of page views will only be available to the item's seller.
    const HonestyStyle="HonestyStyle";//A basic style hit counter (US only). Non-US sites will return errors if they use HonestyStyle as input, and should use BasicStyle instead.
    const NoHitCounter="NoHitCounter";//No hit counter. The number of page views will not be available.
    const RetroStyle="RetroStyle";//A retro, computer-style hit counter.
} 